import axios from 'axios';

const API = axios.create({baseURL: 'https://blogofmine-project.herokuapp.com'});

const url = 'https://blogofmine-project.herokuapp.com/posts';

export const fetchPosts = () => API.get('/posts');
export const createPost = (newPost) => API.post('/posts', newPost);
export const updatePost = (id, updatedData) => API.patch(`${url}/${id}`, updatedData);
export const deletePost = (id) => API.delete(`${url}/${id}`);
export const likePost = (id) => API.patch(`${url}/${id}/likePost`);
